function UserProfileCardRefactored({
  name = "Marie Dubois",
  role = "Développeuse Frontend",
  avatarSrc = "https://images.unsplash.com/photo-1629507208649-70919ca33793?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxwcm9mZXNzaW9uYWwlMjBidXNpbmVzcyUyMHBvcnRyYWl0fGVufDF8fHx8MTc1NzU2NDQ5N3ww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
  onViewProfile,
}) {
  return (
    <article
      role="article"
      aria-label={`Profil de ${name}`}
      className="w-80 h-80 bg-common-white border border-neutral-border rounded-default rounded-tr-none overflow-hidden shadow-sm flex flex-col font-jost"
    >
      <div className="shrink-0">
        <img
          src={avatarSrc}
          alt={`Avatar de ${name}`}
          loading="lazy"
          decoding="async"
          className="object-cover mx-auto w-avatar-md h-avatar-md rounded-full bg-neutral-subtle mt-lg"
        />
      </div>
      <div className="flex-1 flex flex-col p-md">
        <h2 className="font-bold text-title text-neutral-text text-center">{name}</h2>
        <p className="text-body text-neutral-subtle mt-sm text-center">{role}</p>
        <div className="mt-auto">
          <ButtonPrimary
            aria-label={`Voir le profil de ${name}`}
            className="w-full"
            onClick={onViewProfile}
          >
            Voir le profil
          </ButtonPrimary>
        </div>
      </div>
    </article>
  );
}
